DROP DATABASE IF EXISTS FASE_3;

CREATE DATABASE IF NOT EXISTS FASE_3;

USE FASE_3;


CREATE TABLE IF NOT EXISTS PAISES
(
	id INT NOT NULL UNIQUE AUTO_INCREMENT,
	nombre VARCHAR(40) NOT NULL,
    pasaporte BOOLEAN NOT NULL,
    visa BOOLEAN NOT NULL,
	PRIMARY KEY(id)
);

CREATE TABLE IF NOT EXISTS AEROLINEAS
(
	id INT NOT NULL UNIQUE AUTO_INCREMENT,
    nombre VARCHAR(40) NOT NULL,
	PRIMARY KEY(id)
);

CREATE TABLE IF NOT EXISTS AEROPUERTO
(
	nombre VARCHAR(40) UNIQUE NOT NULL,
	direccion VARCHAR(70) NOT NULL,
    codigo_postal INT NOT NULL,
    id_pais INT NOT NULL,
    FOREIGN KEY(id_pais) REFERENCES PAISES(id),
    PRIMARY KEY(nombre)
);

CREATE TABLE IF NOT EXISTS VUELOS
(
	id INT NOT NULL UNIQUE AUTO_INCREMENT,
    puerta_embarque VARCHAR(15) NOT NULL,
    id_aerolinea INT NOT NULL,
    observaciones VARCHAR(20) NOT NULL DEFAULT "A LA HORA",
    FOREIGN KEY(id_aerolinea) REFERENCES AEROLINEAS(id),
	PRIMARY KEY(id)
);


CREATE TABLE IF NOT EXISTS VUELO_AEROPUERTO
(
	id_vuelo INT NOT NULL,
    nombre_aeropuerto VARCHAR(40) NOT NULL,
    FOREIGN KEY(id_vuelo) REFERENCES VUELOS(id),
    FOREIGN KEY(nombre_aeropuerto) REFERENCES AEROPUERTO(nombre),
    PRIMARY KEY(id_vuelo,nombre_aeropuerto)
);

CREATE TABLE IF NOT EXISTS AVION
(
	id INT NOT NULL UNIQUE AUTO_INCREMENT,
    modelo VARCHAR(30) NOT NULL,
	PRIMARY KEY(id)
);


CREATE TABLE IF NOT EXISTS PASAJE
(
	id INT NOT NULL AUTO_INCREMENT UNIQUE,
	precio INT NOT NULL,
    tipo_cabina VARCHAR(30) NOT NULL,
    descuento INT NOT NULL DEFAULT 0,
    precio_original INT NOT NULL DEFAULT 0,
    millas_acumulables INT NOT NULL DEFAULT 0,
    numero_asiento VARCHAR(10) NOT NULL,
    hora_embarque TIME NOT NULL,
    hora_salida TIME NOT NULL,
    hora_llegada TIME NOT NULL,
    fecha_llegada DATE NOT NULL,
    fecha_salida DATE NOT NULL,
    id_pais_destino INT NOT NULL,
    id_pais_inicial INT NOT NULL,
    id_avion INT NOT NULL,
    id_vuelo INT NOT NULL,
    FOREIGN KEY(id_pais_destino) REFERENCES PAISES(id),
    FOREIGN KEY(id_pais_inicial) REFERENCES PAISES(id),
    FOREIGN KEY(id_avion) REFERENCES AVION(id),
	FOREIGN KEY(id_vuelo) REFERENCES VUELOS(id),
    PRIMARY KEY(id)
);

CREATE TABLE IF NOT EXISTS PASAJERO
( 
	id INT NOT NULL AUTO_INCREMENT,
    id_pasaje INT UNIQUE NOT NULL,
	dni VARCHAR(20) UNIQUE NOT NULL,
    numero_viajes INT DEFAULT 0,
	nombres VARCHAR(100)  NOT NULL,
    apellidos VARCHAR(100) NOT NULL,
    sexo BOOLEAN NOT NULL,#VERDADERO HOMBRE, FALSO MUJER
    calle VARCHAR(60) NOT NULL,
    codigo_postal INT NOT NULL,
    numero INT NOT NULL,      
    id_pais INT NOT NULL,
	FOREIGN KEY(id_pasaje) REFERENCES PASAJE(id),
    PRIMARY KEY(id,id_pasaje)
);


CREATE TABLE IF NOT EXISTS PAIS_PASAJERO
(
	id_pais INT NOT NULL,
    id_pasajero INT NOT NULL,
    FOREIGN KEY(id_pais) REFERENCES PAISES(id),
    FOREIGN KEY(id_pasajero) REFERENCES PASAJERO(id),
    PRIMARY KEY(id_pais,id_pasajero)
);

CREATE TABLE IF NOT EXISTS ALIANZA
(
	nombre VARCHAR(40) NOT NULL,
    id_pasajero INT NOT NULL,
    id_aerolinea1 INT NOT NULL,
    id_aerolinea2 INT NOT NULL,
    id_aerolinea3 INT NOT NULL,
    FOREIGN KEY(id_pasajero) REFERENCES PASAJERO(id),
    FOREIGN KEY(id_aerolinea1) REFERENCES AEROLINEAS(id),
    FOREIGN KEY(id_aerolinea2) REFERENCES AEROLINEAS(id),
    FOREIGN KEY(id_aerolinea3) REFERENCES AEROLINEAS(id),
    PRIMARY KEY(nombre,id_pasajero)
);

CREATE TABLE IF NOT EXISTS MILLAS_PASAJERO
(
	id INT UNIQUE NOT NULL,
    millas INT NOT NULL DEFAULT 0,
    FOREIGN KEY(id) REFERENCES PASAJERO(id),
    PRIMARY KEY(id)
);

#vistas

CREATE VIEW Info_Vuelos AS
	SELECT PASAJE.Hora_Salida, AEROLINEAS.nombre, VUELOS.id, PAISES.nombre AS NombrePais, PASAJE.hora_embarque, VUELOS.puerta_embarque, VUELOS.observaciones
    FROM VUELOS
		INNER JOIN PASAJE ON VUELOS.id = PASAJE.id_vuelo
        INNER JOIN AEROLINEAS ON VUELOS.id_aerolinea = AEROLINEAS.id
        INNER JOIN PAISES ON PASAJE.id_pais_destino = PAISES.id;

CREATE VIEW HistorialPasajero as
	SELECT PASAJERO.nombres, PASAJERO.apellidos, PASAJERO.sexo, PASAJERO.dni, MILLAS_PASAJERO.millas, PASAJERO.codigo_postal, PASAJERO.calle, PASAJERO.numero, PASAJERO.numero_viajes
    FROM PASAJERO
		INNER JOIN MILLAS_PASAJERO ON PASAJERO.id = MILLAS_PASAJERO.id;
        
CREATE VIEW REGISTRO_PASAJERO as
	SELECT PASAJERO.nombres, PASAJERO.apellidos, PASAJERO.sexo, PASAJERO.dni, PASAJERO.codigo_postal, PASAJERO.calle, PASAJERO.numero
	FROM PASAJERO;

#---------------------------------------PROCEDIMIENTOS ALMACENADOS-------------------------------------------
#PARA CREAR PASAJES SE TIENE QUE COMPROBAR QUE LA ID DE VUELOS EXISTA
CREATE PROCEDURE InsertarPasaje (id_i INT, precio_i INT, tipo_cabina_i VARCHAR(30), descuento_i INT, millas_acumulables_i INT, numero_asiento_i VARCHAR(10), hora_embarque_i TIME, hora_salida_i TIME, hora_llegada_i TIME, fecha_llegada_i DATE, fecha_salida_i DATE, id_pais_destino_i INT, id_pais_inicial_i INT, id_avion_i INT, id_vuelo_i INT)
INSERT INTO PASAJE VALUES(id_i, precio_i, tipo_cabina_i, descuento_i,0, millas_acumulables_i, numero_asiento_i, hora_embarque_i, hora_salida_i, hora_llegada_i, fecha_llegada_i, fecha_salida_i, id_pais_destino_i, id_pais_inicial_i, id_avion_i, id_vuelo_i);

CREATE PROCEDURE ModificarPasaje (id_i INT, precio_i INT, tipo_cabina_i VARCHAR(30), descuento_i INT, millas_acumulables_i INT, numero_asiento_i VARCHAR(10), hora_embarque_i TIME, hora_salida_i TIME, hora_llegada_i TIME, fecha_llegada_i DATE, fecha_salida_i DATE, id_pais_destino_i INT, id_pais_inicial_i INT, id_avion_i INT, id_vuelo_i INT)
UPDATE PASAJE SET tipo_cabina = tipo_cabina_i, descuento = descuento_i, millas_acumulables = millas_acumulables_i, numero_asiento = numero_asiento_i, hora_embarque = hora_embarque_i, hora_salida = hora_salida_i, hora_llegada = hora_llegada_i, fecha_llegada = fecha_llegada_i, fecha_salida = fecha_salida_i, id_pais_destino = id_pais_destino_i, id_pais_inicial = id_pais_inicial_i, id_avion = id_avion_i, id_vuelo = id_vuelo_i
	WHERE id = id_i;

CREATE PROCEDURE EliminarPasaje (id_i INT)
DELETE FROM PASAJE WHERE id = id_i;
