#CONSULTAS
#consulta 1 (cambiamos la condicion de 120 dolares a 450 dolares, los pasajes estan muy caros :c)

SELECT PASAJE.id, PASAJERO.nombres, PASAJERO.apellidos, PASAJERO.id_pais, PASAJE.precio, PASAJE.fecha_salida
FROM PASAJE 	
	INNER JOIN PASAJERO
    WHERE precio < 450 && fecha_salida BETWEEN '20181001' AND '20181231' && PASAJE.id = PASAJERO.id_pasaje;
	
    
#consulta 2 (extendimos el intervalo de fechas, era temporada baja :c ahora es desde enero a noviembre)

SELECT PASAJE.id, PASAJE.millas_acumulables, MILLAS_PASAJERO.millas, PAISES.visa, PAISES.pasaporte
	FROM PASAJERO
	INNER JOIN PASAJE ON PASAJE.id = PASAJERO.id_pasaje
	INNER JOIN PAISES ON PASAJE.id_pais_inicial = PAISES.id
    INNER JOIN MILLAS_PASAJERO on MILLAS_PASAJERO.id = PASAJERO.id
    WHERE PASAJE.fecha_salida BETWEEN '20180101' AND '20181131' && PAISES.visa = FALSE && PAISES.pasaporte = TRUE && MILLAS_PASAJERO.millas > 2000 && PASAJE.millas_acumulables > 1000;
    
#consulta 3 (se extendio la fecha de busqueda desde septiembre de 2018 a marzo 2019)

SELECT PASAJERO.nombres, PASAJERO.apellidos, PASAJERO.numero_viajes, PASAJE.fecha_salida, PASAJE.precio, PAISES.visa, ALIANZA.nombre
	FROM PASAJERO
	INNER JOIN PASAJE ON PASAJE.id = PASAJERO.id_pasaje
    INNER JOIN PAISES ON PASAJE.id_pais_inicial = PAISES.id
    INNER JOIN ALIANZA ON PASAJERO.id = ALIANZA.id_pasajero
    WHERE PASAJE.fecha_salida BETWEEN '20180901' AND '20190331' && PAISES.visa = false && PASAJE.precio > 100 && PASAJERO.numero_viajes > 2 && ALIANZA.nombre = 'LATAM';



